func (m *UserModel) SaveOrUpdate(user User) (id int64, updated bool, err error) {
	if user.Id > 0 {
		_, _err := m.UpdateById(user.Id, user)
		return user.Id, true, _err
	}

	_id, _err := m.Save(user)
	return _id, false, _err
}

