func (m *PasswordResetModel) First(builders ...query.SQLBuilder) (PasswordReset, error) {
	res, err := m.Get(append(builders, query.Builder().Limit(1))...)
	if err != nil {
		return PasswordReset{}, err
	}

	if len(res) == 0 {
		return PasswordReset{}, query.ErrNoResult
	}

	return res[0], nil
}

